import React, {Component} from 'react';
import {Container, Icon, Search, Button} from 'semantic-ui-react';
import Logo from '../Images/logo.png';
import Logout from "../Createuser/Logout/Logout"
import './Header.css';
import { Route, Switch, Link} from 'react-router-dom';
import Drawer from '../Drawerside/Drawerside'



export default class Header extends Component {

  state = {width:0,
           height: 0,
           opened: false
  }

  sidebar =()=>{
    this.setState({opened: !this.state.opened})
  }
  updateWindowDimensions =()=> {
      this.setState({ width: window.innerWidth, height: window.innerHeight });
    }

  componentDidMount (){
    this.updateWindowDimensions()
    window.addEventListener("resize", this.updateWindowDimensions)
    this.setState({width: window.innerWidth, height: window.innerHeight})
  }



  render(){
    const token = localStorage.getItem("id_token")

    return(
      <div>
      <nav className='container_bar'>
          <ul >
                  <li className='call_number'>
                      CALL US (844) 811-1112
                  </li>

                  <li>
                      {
                        token ?
                        <Logout/> : null
                      }
                  </li>


                  <li>
                      {
                        token ?
                        <Logout/> &&
                        <Link  to="basket">
                            <Icon className='plus_cart'
                             name='plus cart' size='large'/>
                        </Link>
                        :
                        <Link to="login" >
                            <Icon className='user' name='user' size='large'/>
                        </Link>
                      }
                  </li>

                  <li>
                        <Search
                         className='Search'
                         placeholder='search'/>
                  </li>




          </ul>
          </nav>


      <div className="Menu">
            <ul>
              <li> <Link to=''> HOME </Link> </li>
              <li> <Link to='products'> SHOP </Link> </li>
              <li> <Link to="men's"> MEN'S </Link> </li>
              <li> <Link to="women's">WOMEN'S </Link> </li>
            </ul>
      </div>
  </div>
    )
  }}

