import React, {Component} from 'react';
import Customerservices from "./Customerservices";



const customerservices = new Customerservices();

export default class createuser extends Component{

  state={
    name:"",
    Email:"",
    passw:"",
    passwmatch:"",
    nameerror:"",
    Emailerror:"",
    passwerror:"",
    passwmatcherror:"",
    isvalid: false
  }


  userchange =(event)=>{
    this.setState({name: event.target.value})
  }
  emailhandler =(event)=>{
    this.setState({Email:event.target.value})
  }
  passwhandler =(event)=>{
    this.setState({passw:event.target.value})
  }
  passmatchwhandler =(event)=>{
    this.setState({passwmatch:event.target.value})
  }

  validation=()=>{
    if (!this.state.name) {
      this.setState({nameerror: "This field is required"})
      }
    if ( this.state.Email.includes('@') || this.state.Email.includes('.')||this.state.Email.includes('com')) {
      this.setState({Emailerror:"Invalid Email"})
    }
    if (this.state.passw.lenght < 8) {
      this.setState({passwerror:"Password should be at least 8 characs"})
    }
    if (this.state.passwmatch === this.state.passw) {
      this.setState({passwmatcherror:"The pass didn't match"})

    }

  }


  sendingdata =()=>{
    customerservices.createcustomer({
      'name':this.refs.name.value,
      'email':this.refs.email.value,
      'password':this.refs.password.value
    }).then((result)=> {
      console.log(result);
      alert("Created");
    }).catch((error)=>{
      console.log(error);
      alert("not good");
    })

}
  submithandle=(e)=>{
    e.preventDefault();
    this.sendingdata();
  };



  render(){
    return(

       <form onSubmit={this.submithandle}>
       <div>
         <h> Register </h>
            <p>Username</p>
               <input
                   ref='name'
                   type="text"
                   placeholder="User"
                   value={this.state.name}
                   onChange={this.userchange}/>
            <div style={{color: "red"}}> {this.state.nameerror }</div>
            <p> Email </p>
               <input
                   ref='email'
                   type="text"
                   placeholder="Email"
                   value={this.state.Email}
                   onChange={this.emailhandler}/>
             <p> Password </p>
                <input
                    ref='password'
                    type="Password"
                    placeholder="Password"
                    value={this.state.Passw}
                    onChange={this.passwhandler}/>
            <p> Password </p>
               <input
                   type="Password"
                   placeholder="confirm Password"
                   value={this.state.passwmatch}
                   onChange={this.passmatchwhandler}/>
             <button> Register </button>








        </div>
      </form>
    )
  }
}
