import {React, Component} from 'react';
import {Container, Icon, Search} from 'semantic-ui-react';
import Logo from '../Images/logo.png';
import Logout from "../Createuser/Logout/Logout"
import './Header.css';
import { Route, Switch, Link} from 'react-router-dom';
import Drawer from '../Drawerside/Drawerside'



export default class Header extends Component {

  state = {width:0, height: 0}


  updateWindowDimensions =()=> {
      this.setState({ width: window.innerWidth, height: window.innerHeight });
    }

  componentDidMount (){
    this.updateWindowDimensions()
    this.setState({width: window.innerWidth, height: window.innerHeight})
  }



  render(){
    let token = localStorage.getItem("id_token")
    return(
      <div>
      {this.state.width <= 767 ? <div> <Drawer/> </div> : null}
        <div className='bar'>
          <img className='Logo' src={Logo} alt='Logo'/>
          <h className='call_number'> CALL US (844) 811-1112 </h>
           <div>
            {
              token ? <Link className='plus_cart' to="basket">
                      <Icon  name='plus cart' size='large'/></Link>
                       : null
            }
            </div>
            <div className='Log_out'>

            {
              token ? <Logout/> :
              <Link   to="login" >
                  <Icon className='user' name='user' size='large'/>
              </Link>
            }
            </div>
            <Search className='Search'
                   placeholder='search'
                // loading={isLoading}
                // onResultSelect={this.handleResultSelect}
                // onSearchChange={_.debounce(this.handleSearchChange, 500, {
                //   leading: true,
                // })}
                // results={results}
                // value={value}
                // {...this.props}
              />
        </div>

        <div className="Menu">
        <ul>
          <li> <Link to=''> HOME </Link> </li>
          <li> <Link to='products'> SHOP </Link> </li>
          <li> <Link to="men's"> MEN'S </Link> </li>
          <li> <Link to="women's">WOMEN'S </Link> </li>
          <li> <Link to='#'>SHOP BY BRAND </Link> </li>
        </ul>

        </div>

        </div>
    )
  }

  }






