import React, {Component} from 'react';
import axios from 'axios';
import {Card, Icon, Image, Segment, Grid, Header, Rating, Button} from 'semantic-ui-react'


export default class Cart extends Component{

  state ={
    data:[],
    items:[],
    products:[]
  }
// this componentWillMount is excuted before the render method
  componentWillMount(){
    let url="http://127.0.0.1:8000/cartItems"
    let token = localStorage.getItem('id_token')
    axios.get(url, {
    headers: {
        'Authorization': 'Token '+ token,
        'Accept' : 'application/json',
        'Content-Type': 'application/json'}})
    .then((res)=>{

      this.setState({data: res.data});
      console.log(res.data);
})}





  render(){
    const data_map =


      this.state.data.map((res)=> {
        return(

      <Segment style={{padding: '2em 0em', minHeight: 900}} vertical>
                  <Grid container stackable verticalAlign='middle'>
                      <Grid.Row>
                          <Grid.Column width={4}>
                              <Image src={res.product.image}/>
                          </Grid.Column>
                          <Grid.Column width={12}>
                              <Header as="h1">{res.product.name}</Header>
                              <Header as="h2">Price: {res.product.price}</Header>
                              <Header as="h4">Description: {res.product.description}</Header>
                              <Rating icon='star' defaultRating={5} maxRating={5}/>
                              <Header as="h4" className='Add'>
                              </Header>
                          </Grid.Column>
                      </Grid.Row>
                  </Grid>
        </Segment>
  )})


    return(

      <p>
         {data_map}
      </p>
    )
  }
}
