import React, {Component} from 'react';
import customerservices from "./Customerservices";





export default class createuser extends Component{







  sendingdata =()=>{
    customerservices.createcustomer({
      "user":this.user.ref.value,
      "email":this.refs.Email.value,
      "password":this.refs.passw.value
    }).then((result)=> {
      alert("Created");
    }).catch((errors)=>{
      alert("Error");
    });

}
  submithandle=(e)=>{
    e.preventDefault();
    this.sendingdata();
  };



  render(){
    return(

       <form onSubmit={this.submithandle}>
       <div>
         <h> Register </h>
            <p>Username</p>
               <input
                   type="text"
                   placeholder="User"
                   ref="Username"
                   />
            <p> Email </p>
               <input
                   type="text"
                   placeholder="Email"
                   ref="Email"
                   />
             <p> Password </p>
                <input
                    type="Password"
                    placeholder="Password"
                    ref="Password"
                    />
             <button> Register </button>








        </div>
      </form>
    )
  }
}
