      this.setState({search: event.target.value},
        () => {
          if (this.state.search && this.state.search.length > 1) {

                  this.search()



          }

            }
        )}
















  render() {
    const {search_data} = this.state;


    return (


      <div>

              <input
                type="text"
                value={this.state.search}
                onChange={this.searchchangehandler}
               />











      </div>




)}
}
